import React, { useState, useEffect } from "react";
import axios from "axios";
import "../styles/invoice_list.css"; // Ensure to link the CSS file

const InvoiceList = () => {
  const [dueInvoices, setDueInvoices] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    // Fetch only due invoices
    axios
      .get("http://localhost:8080/invoices?status=due", { withCredentials: true })
      .then((response) => {
        setDueInvoices(response.data);
        setLoading(false);
      })
      .catch((error) => {
        console.error("Error fetching due invoices:", error);
        setLoading(false);
      });
  }, []);

  const triggerEmailNotification = (invoice) => {
    // Trigger email via Zapier webhook
    axios
      .post("https://hooks.zapier.com/hooks/catch/your-zapier-webhook-url/", { invoice })
      .then(() => {
        alert("Email notification sent successfully!");
      })
      .catch((error) => {
        console.error("Error triggering email notification:", error);
        alert("Failed to send email notification.");
      });
  };

  if (loading) {
    return <div className="loader-container"><div className="loader"></div><p>Loading invoices...</p></div>;
  }

  return (
    <div className="reminders-container">
      <h1>Due Invoices</h1>
      {dueInvoices.length === 0 ? (
        <p>No due invoices available.</p>
      ) : (
        <div className="invoices-section">
          {dueInvoices.map((invoice) => (
            <div key={invoice._id} className="invoice-card">
              <div>
                <h3>{invoice.customer}</h3>
                <p><strong>Amount:</strong> ${invoice.amount.toFixed(2)}</p>
                <p className="due-date"><strong>Due Date:</strong> {new Date(invoice.dueDate).toLocaleDateString()}</p>
                <span className="status">{invoice.status}</span>
              </div>
              <button onClick={() => triggerEmailNotification(invoice)} className="trigger-btn">
                Send Reminder
              </button>
            </div>
          ))}
        </div>
      )}
    </div>
  );
};

export default InvoiceList;
