import React from 'react';
import { GoogleLogin } from '@react-oauth/google'; // Use this for client-side OAuth
import { useNavigate } from 'react-router-dom';
import '../styles/landingpage.css';

const LandingPage = () => {
  const navigate = useNavigate();

  // This function handles successful Google login
  const handleGoogleLogin = (response) => {
    // Call handleLogin from App.js to set user data
    const userInfo = response?.credential ? JSON.parse(atob(response.credential.split('.')[1])) : null;
    if (userInfo) {
      navigate('/homepage/dashboard'); // Redirect to dashboard after successful login
    }
  };

  return (
    <div className="landing-page">
      {/* Hero Section */}
      <header className="hero">
        <div className="hero-content">
          <h1>Welcome to InvoiceQuick</h1>
          <p>Your all-in-one solution to manage invoices and get paid faster!</p>
          
          {/* Google login button */}
          <GoogleLogin 
            onSuccess={handleGoogleLogin}
            onError={(error) => console.error('Login Failed:', error)}
            useOneTap // Optional: Automatically show Google One Tap login on supported devices
          />
        </div>
        <div className="hero-image">
          <img src="/images/tensor.png" alt="SaaS Illustration" />
        </div>
      </header>

      {/* Features Section */}
      <section className="features">
        <h2>Why Choose Us?</h2>
        <div className="features-list">
          <div className="feature-item">
            <h3>Automated Invoicing</h3>
            <p>Save time by automating your invoicing processes.</p>
          </div>
          <div className="feature-item">
            <h3>Payment Reminders</h3>
            <p>Ensure timely payments with smart reminders.</p>
          </div>
          <div className="feature-item">
            <h3>Real-Time Analytics</h3>
            <p>Track your invoices and payments with ease.</p>
          </div>
        </div>
      </section>

      {/* Testimonials Section */}
      <section className="testimonials">
        <h2>What Our Customers Say</h2>
        <div className="testimonial">
          <p>"InvoiceQuick has transformed how I manage my business finances!"</p>
          <span>- Jane Doe</span>
        </div>
      </section>

      {/* Footer */}
      <footer className="footer">
        <p>Â© 2024 InvoiceQuick. All rights reserved.</p>
      </footer>
    </div>
  );
};

export default LandingPage;
