// frontend/src/components/invoice_list.js
import React, { useEffect, useState } from 'react';
import axios from 'axios';

const InvoiceList = () => {
  const [invoices, setInvoices] = useState([]);
  const [error, setError] = useState('');

  useEffect(() => {
    // Fetch the invoices from the backend API
    axios.get('http://localhost:5000/invoice') // Make sure the URL matches the backend route
      .then(response => {
        setInvoices(response.data);  // Set the fetched invoices into the state
      })
      .catch(err => {
        setError('Failed to fetch invoices: ' + err.message);  // Handle errors
      });
  }, []);

  return (
    <div>
      <h1>Invoice List</h1>
      {error && <div style={{ color: 'red' }}>{error}</div>}
      <ul>
        {invoices.map(invoice => (
          <li key={invoice.id}>
            {invoice.name} - ${invoice.amount}
          </li>
        ))}
      </ul>
    </div>
  );
};

export default InvoiceList;
