import React, { useState } from 'react';
import { BrowserRouter as Router, Routes, Route, Navigate } from 'react-router-dom';
import { GoogleOAuthProvider, GoogleLogin } from '@react-oauth/google'; // Import the GoogleOAuthProvider and GoogleLogin component
import LandingPage from './components/landingpage';
import HomePage from './components/homepage'; 
import InvoiceList from './components/invoice_list'; 
import Dashboard from './components/dashboard';     
import { useNavigate } from 'react-router-dom'; // Import useNavigate

const App = () => {
  const [user, setUser] = useState(null); // State to store user data after login
  const navigate = useNavigate(); // Initialize the useNavigate hook

  const handleLogin = (response) => {
    // Get the user info from the response
    const userInfo = response?.credential ? JSON.parse(atob(response.credential.split('.')[1])) : null;
    setUser(userInfo); // Set user info to state

    // After login, navigate to the homepage and invoices
    navigate('/homepage/invoices');
  };

  return (
    <GoogleOAuthProvider clientId="YOUR_GOOGLE_CLIENT_ID">
      <Router>
        <div>
          <Routes>
            {/* Redirect root to landing page */}
            <Route path="/" element={<Navigate to="/landingpage" />} />

            {/* Landing Page */}
            <Route path="/landingpage" element={<LandingPage />} />

            {/* HomePage with Sidebar */}
            <Route path="/homepage/*" element={<HomePage user={user} />}>
              <Route path="dashboard" element={<Dashboard />} />
              <Route path="invoices" element={<InvoiceList />} />
            </Route>

            {/* Google Login */}
            <Route path="/login" element={
              <GoogleLogin 
                onSuccess={handleLogin} // Handle login success
                onError={(error) => console.error('Login Failed:', error)} // Handle login failure
              />
            } />
          </Routes>
        </div>
      </Router>
    </GoogleOAuthProvider>
  );
};

export default App;
