import React, { useState, useEffect } from 'react';
import axios from 'axios';
import '../styles/reminders.css'; // Updated styles for better UI/UX

const Reminders = () => {
  const [dueInvoices, setDueInvoices] = useState([]);
  const [loading, setLoading] = useState(true);
  const [reminders, setReminders] = useState({
    reminder1: true,
    reminder2: true,
    reminder3: true,
    reminder4: true,
    reminder5: true
  });

  // Fetch due invoices
  useEffect(() => {
    axios.get('http://localhost:8080/invoices?status=due', { withCredentials: true })
      .then(response => {
        setDueInvoices(response.data);
        setLoading(false);
      })
      .catch(error => {
        console.error('Error fetching due invoices:', error);
        setLoading(false);
      });
  }, []);

  // Handle toggling reminder status
  const handleReminderToggle = (reminder) => {
    setReminders(prevState => ({
      ...prevState,
      [reminder]: !prevState[reminder]
    }));
  };

  // Handle manual trigger for reminder automation
  const triggerAutomation = () => {
    axios.post('http://localhost:8080/trigger-automation', { invoices: dueInvoices })
      .then(() => {
        alert('Automation triggered successfully!');
      })
      .catch(error => {
        console.error('Error triggering automation:', error);
        alert('Failed to trigger automation.');
      });
  };

  if (loading) {
    return (
      <div className="loader-container">
        <div className="loader"></div>
        <p>Loading reminders...</p>
      </div>
    );
  }

  return (
    <div className="reminders-container">
      <div className="reminders-header">
        <h1>Invoice Reminders</h1>
        <p>Keep track of your invoices and send automated reminders to ensure timely payments.</p>
      </div>

      <div className="reminders-section">
        <h2>Automated Reminder Settings</h2>
        <p>Toggle the reminders below to enable or disable them for overdue invoices.</p>
        <div className="reminders-list">
          {['reminder1', 'reminder2', 'reminder3', 'reminder4', 'reminder5'].map((reminder, index) => (
            <div key={index} className="reminder-card">
              <div className="reminder-info">
                <span>Reminder {index + 1}</span>
                <button className="edit-button">Edit</button>
              </div>
              <label className="switch">
                <input
                  type="checkbox"
                  checked={reminders[reminder]}
                  onChange={() => handleReminderToggle(reminder)}
                />
                <span className="slider round"></span>
              </label>
            </div>
          ))}
        </div>
      </div>

      <div className="invoices-section">
        <h2>Due Invoices</h2>
        <div className="due-invoices">
          {dueInvoices.length === 0 ? (
            <p className="no-invoices">No due invoices found. Great job staying on top of things!</p>
          ) : (
            dueInvoices.map(invoice => (
              <div key={invoice._id} className="invoice-card">
                <div className="invoice-header">
                  <h3>{invoice.customer}</h3>
                  <span className="status-badge">{invoice.status}</span>
                </div>
                <p><strong>Amount:</strong> ${invoice.amount.toFixed(2)}</p>
                <p><strong>Due Date:</strong> {invoice.dueDate ? new Date(invoice.dueDate).toLocaleDateString() : 'Not Available'}</p>
              </div>
            ))
          )}
        </div>
      </div>

      <div className="automation-section">
        <button onClick={triggerAutomation} className="trigger-button">Trigger Automation</button>
      </div>
    </div>
  );
};

export default Reminders;
