import React, { useState, useEffect } from 'react';
import axios from 'axios';
import '../styles/reminders.css'; // Add CSS for styling

const Reminders = () => {
  const [pastDueInvoices, setPastDueInvoices] = useState([]);
  const [zapierWebhookUrl, setZapierWebhookUrl] = useState('');
  const [message, setMessage] = useState('');

  // Fetch past-due invoices on component load
  useEffect(() => {
    fetchPastDueInvoices();
  }, []);

  const fetchPastDueInvoices = async () => {
    try {
      const response = await axios.get('/api/reminders/past-due'); // Adjust API endpoint if needed
      setPastDueInvoices(response.data);
    } catch (error) {
      console.error('Error fetching past-due invoices:', error);
    }
  };

  const triggerZapier = async () => {
    try {
      const response = await axios.post('/api/reminders/trigger-zapier', { zapierWebhookUrl });
      setMessage(response.data.message);
    } catch (error) {
      console.error('Error triggering Zapier:', error);
      setMessage('Failed to trigger Zapier.');
    }
  };

  return (
    <div className="reminder-page">
      <h1>Reminders</h1>
      {message && <p className="message">{message}</p>}
      
      <div className="zapier-config">
        <label htmlFor="zapierWebhook">Zapier Webhook URL:</label>
        <input
          type="text"
          id="zapierWebhook"
          value={zapierWebhookUrl}
          onChange={(e) => setZapierWebhookUrl(e.target.value)}
          placeholder="Enter Zapier Webhook URL"
        />
        <button onClick={triggerZapier}>Trigger Zapier Automation</button>
      </div>

      <h2>Past-Due Invoices</h2>
      <ul className="invoice-list">
        {pastDueInvoices.map((invoice) => (
          <li key={invoice._id}>
            <strong>{invoice.customer}</strong>: {invoice.amount} due on {new Date(invoice.dueDate).toLocaleDateString()}
          </li>
        ))}
      </ul>
    </div>
  );
};

export default Reminders;
