// src/components/HomePage.js
import React from 'react';
import { useNavigate } from 'react-router-dom';
import { GoogleLogin } from '@react-oauth/google';  // Import GoogleLogin component
import '../styles/homepage.css';

const HomePage = () => {
  const navigate = useNavigate();

  // Handle button click to start management
  const handleStartManagement = () => {
    navigate('/invoices');
  };

  // Handle the response from Google login
  const handleLoginSuccess = (response) => {
    const token = response.credential; // Get the Google OAuth token
    console.log('Logged in with Google! Token:', token);
    
    // Send the token to your backend for verification
    // Example: POST the token to your backend
    fetch('/auth/google', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify({ token }),
    })
      .then((res) => res.json())
      .then((data) => {
        if (data.success) {
          // Navigate to the invoices page after successful login
          navigate('/invoices');
        }
      })
      .catch((error) => {
        console.error('Error during login:', error);
      });
  };

  const handleLoginFailure = (error) => {
    console.error('Login failed:', error);
  };

  return (
    <div className="home-page-container">
      <h1 className="home-page-header">Welcome to Invoice Management System</h1>

      <button className="home-page-button" onClick={handleStartManagement}>
        Start Management
      </button>

      {/* Google Login Button */}
      <div className="google-login-container">
        <GoogleLogin 
          onSuccess={handleLoginSuccess}
          onError={handleLoginFailure}
        />
      </div>
    </div>
  );
};

export default HomePage;
