// src/components/invoice_list.js
import React, { useEffect, useState } from 'react';
import '../styles/invoice_list.css';  // Link to the existing CSS file

const InvoiceList = () => {
  const [invoices, setInvoices] = useState([]);

  useEffect(() => {
    fetch('http://localhost:4000/invoice')
      .then((response) => response.json())
      .then((data) => setInvoices(data))
      .catch((error) => console.log('Error fetching invoices:', error));
  }, []);

  return (
    <div className="invoice-list-container">
      <h2 className="invoice-list-header">Invoice List</h2>
      <table className="invoice-list-table">
        <thead>
          <tr>
            <th>ID</th>
            <th>Customer</th>
            <th>Amount</th>
            <th>Status</th>
          </tr>
        </thead>
        <tbody>
          {invoices.map((invoice) => (
            <tr key={invoice.id}>
              <td>{invoice.id}</td>
              <td>{invoice.customer}</td>
              <td>{invoice.amount}</td>
              <td>{invoice.status}</td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default InvoiceList;
