import React, { useState, useEffect } from "react";
import axios from "axios";
import { FiMail } from "react-icons/fi";
import "../styles/reminders.css"; // Reference to the professional styling

const Reminders = () => {
  const [dueInvoices, setDueInvoices] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    // Fetch only due invoices
    axios
      .get("http://localhost:8080/invoices", { withCredentials: true }) // Replace with your API endpoint
      .then((response) => {
        // Filter only due invoices
        const filteredInvoices = response.data.filter((invoice) => invoice.status === "due");
        setDueInvoices(filteredInvoices);
        setLoading(false);
      })
      .catch((error) => {
        console.error("Error fetching invoices:", error);
        setLoading(false);
      });
  }, []);

  const sendReminder = (invoiceId) => {
    // Trigger Zapier webhook for a specific invoice
    axios
      .post(
        "https://hooks.zapier.com/hooks/catch/your-webhook-id/", // Replace with Zapier webhook
        { invoiceId },
        { headers: { "Content-Type": "application/json" } }
      )
      .then(() => {
        alert("Reminder sent successfully!");
      })
      .catch((error) => {
        console.error("Error sending reminder:", error);
        alert("Failed to send reminder.");
      });
  };

  if (loading) {
    return (
      <div className="loader-container">
        <div className="loader"></div>
        <p>Loading due invoices...</p>
      </div>
    );
  }

  return (
    <div className="reminders-container">
      <h1>Due Invoices</h1>
      <p>Below is a list of invoices with due status. Send reminders to notify customers.</p>

      {dueInvoices.length === 0 ? (
        <p className="no-invoices">No due invoices. Great job staying on top of payments!</p>
      ) : (
        <div className="invoices-grid">
          {dueInvoices.map((invoice) => (
            <div key={invoice._id} className="invoice-card">
              <div className="invoice-header">
                <h3>{invoice.customer}</h3>
                <span className="invoice-status due">Due</span>
              </div>
              <p><strong>Amount:</strong> ${invoice.amount.toFixed(2)}</p>
              <p><strong>Due Date:</strong> {new Date(invoice.dueDate).toLocaleDateString()}</p>
              <button
                className="reminder-btn"
                onClick={() => sendReminder(invoice._id)}
              >
                <FiMail /> Send Reminder
              </button>
            </div>
          ))}
        </div>
      )}
    </div>
  );
};

export default Reminders;
