import React from 'react';
import '../styles/homepage.css'; // Import the CSS file for styling

const HomePage = () => {
  const handleGoogleLogin = () => {
    // Redirect to the backend Google login route
    window.location.href = 'http://localhost:4000/auth/google';
  };

  return (
    <div className="homepage">
      <header className="homepage-header">
        <h1>Invoice Reminder System</h1>
        <p>Stay on top of your invoices with ease.</p>
      </header>
      
      <main className="homepage-main">
        <div className="homepage-content">
          <h2>Welcome Back!</h2>
          <p>Log in to view your due invoices and automate reminders seamlessly.</p>
          <button className="google-login-btn" onClick={handleGoogleLogin}>
            <img 
              src="/images/googleicon.png" 
              alt="Google Icon" 
              className="google-icon" 
            />
            Login with Google
          </button>
        </div>
      </main>

      <footer className="homepage-footer">
        <p>&copy; {new Date().getFullYear()} Invoice Reminder System. All rights reserved.</p>
      </footer>
    </div>
  );
};

export default HomePage;
