import React, { useState, useEffect } from "react";
import axios from "axios";
import { FiEdit, FiCheckSquare, FiBell } from "react-icons/fi";
import "../styles/reminders.css"; // Assuming similar CSS file

const Reminders = () => {
  const [dueInvoices, setDueInvoices] = useState([]);
  const [loading, setLoading] = useState(true);
  const [reminders, setReminders] = useState([
    { id: 1, title: "Reminder 1", description: "7 days before due date", active: true },
    { id: 2, title: "Reminder 2", description: "On due date", active: true },
    { id: 3, title: "Reminder 3", description: "7 days after due date", active: true },
    { id: 4, title: "Reminder 4", description: "14 days after due date", active: true },
    { id: 5, title: "Pre-Collections Reminder", description: "60 days after due date", active: true },
  ]);

  useEffect(() => {
    axios
      .get("http://localhost:8080/invoices?status=due", { withCredentials: true })
      .then((response) => {
        setDueInvoices(response.data);
        setLoading(false);
      })
      .catch((error) => {
        console.error("Error fetching due invoices:", error);
        setLoading(false);
      });
  }, []);

  const handleReminderToggle = (id) => {
    setReminders((prevReminders) =>
      prevReminders.map((reminder) =>
        reminder.id === id ? { ...reminder, active: !reminder.active } : reminder
      )
    );
  };

  const handleEditReminder = (id) => {
    const updatedDescription = prompt("Enter a new description for this reminder:");
    if (updatedDescription) {
      setReminders((prevReminders) =>
        prevReminders.map((reminder) =>
          reminder.id === id ? { ...reminder, description: updatedDescription } : reminder
        )
      );
    }
  };

  const triggerAutomation = () => {
    axios
      .post("http://localhost:8080/trigger-automation", { invoices: dueInvoices })
      .then(() => {
        alert("Automation triggered successfully!");
      })
      .catch((error) => {
        console.error("Error triggering automation:", error);
        alert("Failed to trigger automation.");
      });
  };

  if (loading) {
    return (
      <div className="loader-container">
        <div className="loader"></div>
        <p>Loading reminders...</p>
      </div>
    );
  }

  return (
    <div className="reminders-container">
      <h1>Automated Invoice Reminders</h1>
      <p>Configure automated reminders to notify customers about overdue invoices.</p>

      {/* Reminders List */}
      <div className="reminders-list">
        {reminders.map((reminder) => (
          <div
            key={reminder.id}
            className={`reminder-card ${reminder.active ? "active" : ""}`}
          >
            <div className="reminder-info">
              <FiBell size={20} className="icon" />
              <div>
                <h3>{reminder.title}</h3>
                <p>{reminder.description}</p>
              </div>
            </div>
            <div className="reminder-actions">
              <button onClick={() => handleEditReminder(reminder.id)} className="edit-btn">
                <FiEdit /> Edit
              </button>
              <label className="switch">
                <input
                  type="checkbox"
                  checked={reminder.active}
                  onChange={() => handleReminderToggle(reminder.id)}
                />
                <span className="slider round"></span>
              </label>
            </div>
          </div>
        ))}
      </div>

      {/* Trigger Automation Button */}
      <button onClick={triggerAutomation} className="trigger-btn">
        <FiCheckSquare /> Trigger Automation
      </button>

      {/* Due Invoices Section */}
      <div className="invoices-section">
        <h2>Due Invoices</h2>
        {dueInvoices.length === 0 ? (
          <p>No due invoices. Keep up the great work!</p>
        ) : (
          dueInvoices.map((invoice) => (
            <div key={invoice._id} className="invoice-card">
              <h3>{invoice.customer}</h3>
              <p><strong>Amount:</strong> ${invoice.amount.toFixed(2)}</p>
              <p><strong>Due Date:</strong> {new Date(invoice.dueDate).toLocaleDateString()}</p>
              <span className="status">{invoice.status}</span>
            </div>
          ))
        )}
      </div>
    </div>
  );
};

export default Reminders;
