import React, { useState, useEffect } from "react";
import axios from "axios";
import { FiCheckSquare } from "react-icons/fi";
import "../styles/reminders.css";

const Reminders = () => {
  const [dueInvoices, setDueInvoices] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    axios
      .get("http://localhost:8080/invoices", { withCredentials: true })
      .then((response) => {
        const filteredInvoices = response.data.filter(
          (invoice) => invoice.status === "due"
        );
        setDueInvoices(filteredInvoices);
        setLoading(false);
      })
      .catch((error) => {
        console.error("Error fetching invoices:", error);
        setLoading(false);
      });
  }, []);

  const triggerAutomation = () => {
    if (dueInvoices.length === 0) {
      alert("No due invoices to send reminders for.");
      return;
    }

    axios
      .post(
        "https://hooks.zapier.com/hooks/catch/your-webhook-id/",
        { invoices: dueInvoices },
        { headers: { "Content-Type": "application/json" } }
      )
      .then(() => {
        alert("Emails have been sent successfully!");
      })
      .catch((error) => {
        console.error("Error triggering automation:", error);
        alert("Failed to trigger automation.");
      });
  };

  if (loading) {
    return (
      <div className="loader-container">
        <div className="loader"></div>
        <p>Loading reminders...</p>
      </div>
    );
  }

  return (
    <div className="reminders-container">
      <header className="reminders-header">
        <h1>Automated Invoice Reminders</h1>
        <p>Never miss a payment! Automate reminders for due invoices effortlessly.</p>
      </header>

      <main>
        <section className="reminders-list">
          <h2>Reminder Schedule</h2>
          <div className="reminder-card">
            <h3>Reminder 1</h3>
            <p>7 days before the due date.</p>
          </div>
          <div className="reminder-card">
            <h3>Reminder 2</h3>
            <p>On the due date.</p>
          </div>
          <div className="reminder-card">
            <h3>Reminder 3</h3>
            <p>Every 2 days after the due date.</p>
          </div>
        </section>

        <button onClick={triggerAutomation} className="trigger-btn">
          <FiCheckSquare /> Trigger Email Reminders
        </button>

        <section className="invoices-section">
          <h2>Due Invoices</h2>
          {dueInvoices.length === 0 ? (
            <p className="no-invoices">No due invoices. Keep up the great work!</p>
          ) : (
            <div className="invoices-grid">
              {dueInvoices.map((invoice) => (
                <div key={invoice._id} className="invoice-card">
                  <h3>{invoice.customer}</h3>
                  <p>
                    <strong>Amount:</strong> ${invoice.amount.toFixed(2)}
                  </p>
                  <p>
                    <strong>Due Date:</strong> {new Date(invoice.dueDate).toLocaleDateString()}
                  </p>
                  <span className="status">{invoice.status}</span>
                </div>
              ))}
            </div>
          )}
        </section>
      </main>
    </div>
  );
};

export default Reminders;
