import React, { useState } from 'react';
import { Routes, Route, Navigate } from 'react-router-dom';
import { GoogleOAuthProvider, GoogleLogin } from '@react-oauth/google';
import LandingPage from './components/landingpage';
import HomePage from './components/homepage';
import InvoiceList from './components/invoice_list';
import Dashboard from './components/dashboard';
import Reminders from './components/reminders';
import { useNavigate } from 'react-router-dom';

const App = () => {
  const [user, setUser] = useState(null);
  const navigate = useNavigate();

  const handleLogin = (response) => {
    try {
      const userInfo = response?.credential ? JSON.parse(atob(response.credential.split('.')[1])) : null;

      if (userInfo) {
        setUser(userInfo);
        navigate('/homepage/invoices'); // Redirect to invoices on successful login
      } else {
        console.error('Invalid response:', response);
      }
    } catch (error) {
      console.error('Login processing error:', error);
    }
  };

  const handleLogout = () => {
    setUser(null); // Clear user state
    navigate('/landingpage'); // Redirect to landing page
  };

  return (
    <GoogleOAuthProvider clientId={process.env.REACT_APP_GOOGLE_CLIENT_ID}> {/* Use .env variable */}
      <Routes>
        {/* Redirect root to landing page */}
        <Route path="/" element={<Navigate to="/landingpage" />} />

        {/* Landing Page */}
        <Route path="/landingpage" element={<LandingPage />} />

        {/* Google Login Route */}
        <Route 
          path="/login" 
          element={
            <GoogleLogin 
              onSuccess={handleLogin} 
              onError={(error) => alert('Login Failed! Please try again.')} 
            />
          } 
        />

        {/* Protected Routes */}
        <Route 
          path="/homepage/*" 
          element={
            user ? (
              <HomePage user={user} onLogout={handleLogout} /> // Pass logout function to HomePage
            ) : (
              <Navigate to="/login" />
            )
          }
        >
          <Route path="dashboard" element={<Dashboard />} />
          <Route path="invoices" element={<InvoiceList />} />
          <Route path="reminders" element={<Reminders />} />
        </Route>
      </Routes>
    </GoogleOAuthProvider>
  );
};

export default App;
