import React, { useState, useEffect } from "react";
import axios from "axios";
import { FiMail, FiCalendar } from "react-icons/fi";
import "../styles/reminde.css"; // Ensure this CSS has professional styling

const Reminders = () => {
  const [dueInvoices, setDueInvoices] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    // Fetch invoices from the backend
    axios
      .get("http://localhost:8080/invoices", { withCredentials: true }) // Replace with your actual endpoint
      .then((response) => {
        // Filter only due invoices
        const filteredInvoices = response.data.filter((invoice) => invoice.status === "due");
        setDueInvoices(filteredInvoices);
        setLoading(false);
      })
      .catch((error) => {
        console.error("Error fetching invoices:", error);
        setLoading(false);
      });
  }, []);

  const triggerAutomation = () => {
    if (dueInvoices.length === 0) {
      alert("No due invoices to send reminders for.");
      return;
    }

    // Send filtered invoices to Zapier webhook
    axios
      .post(
        "https://hooks.zapier.com/hooks/catch/your-webhook-id/", // Replace with your Zapier webhook URL
        { invoices: dueInvoices },
        { headers: { "Content-Type": "application/json" } }
      )
      .then(() => {
        alert("Emails have been sent successfully!");
      })
      .catch((error) => {
        console.error("Error triggering automation:", error);
        alert("Failed to trigger automation.");
      });
  };

  if (loading) {
    return (
      <div className="loader-container">
        <div className="loader"></div>
        <p>Loading reminders...</p>
      </div>
    );
  }

  return (
    <div className="reminders-container">
      <h1>Automated Invoice Reminders</h1>
      <p>Review and send reminders for due invoices.</p>

      <button onClick={triggerAutomation} className="trigger-btn">
        <FiMail /> Send Email Reminders
      </button>

      {/* Due Invoices Section */}
      <div className="invoices-section">
        <h2>Due Invoices</h2>
        {dueInvoices.length === 0 ? (
          <p className="no-invoices-message">No due invoices at the moment. You're all set!</p>
        ) : (
          <div className="invoice-grid">
            {dueInvoices.map((invoice) => (
              <div key={invoice._id} className="invoice-card">
                <div className="invoice-header">
                  <h3>{invoice.customer}</h3>
                  <span className={`status ${invoice.status}`}>{invoice.status}</span>
                </div>
                <div className="invoice-details">
                  <p><strong>Amount:</strong> ${invoice.amount.toFixed(2)}</p>
                  <p><FiCalendar /> <strong>Due Date:</strong> {new Date(invoice.dueDate).toLocaleDateString()}</p>
                </div>
              </div>
            ))}
          </div>
        )}
      </div>
    </div>
  );
};

export default Reminders;
