import React, { useState, useEffect } from 'react';

const InvoiceList = () => {
    const [invoices, setInvoices] = useState([]);
    const [loading, setLoading] = useState(true);

    useEffect(() => {
        const fetchInvoices = async () => {
            try {
                const response = await fetch('/api/invoices'); // Adjust URL if necessary
                const data = await response.json();
                if (response.ok) {
                    setInvoices(data.data); // Store the invoices in state
                } else {
                    console.error('Error fetching invoices');
                }
            } catch (error) {
                console.error('Error:', error);
            } finally {
                setLoading(false);
            }
        };

        fetchInvoices();
    }, []);

    if (loading) {
        return <div>Loading...</div>;
    }

    return (
        <div>
            <h2>Invoices</h2>
            {invoices.length === 0 ? (
                <p>No invoices found.</p>
            ) : (
                <ul>
                    {invoices.map(invoice => (
                        <li key={invoice._id}>{invoice.name}</li> // Adjust this to match your data structure
                    ))}
                </ul>
            )}
        </div>
    );
};

export default InvoiceList;
