import React, { useEffect, useState } from 'react'; 
import axios from 'axios';
import '../styles/invoice_list.css'; // Adjusted the path to reflect the location of the CSS file


const InvoiceList = () => {
  const [invoices, setInvoices] = useState([]);
  const [filteredInvoices, setFilteredInvoices] = useState([]);
  const [loading, setLoading] = useState(true);
  const [filter, setFilter] = useState('all');

  useEffect(() => {
    // Fetch invoices from the backend
    axios.get('http://localhost:6000/invoices', { withCredentials: true })
      .then(response => {
        if (response.data && Array.isArray(response.data)) {
          setInvoices(response.data);
          setFilteredInvoices(response.data);  // Initially, all invoices are shown
        } else {
          console.error('Invalid invoice data:', response.data);
        }
        setLoading(false);
      })
      .catch(error => {
        console.error('Error fetching invoices:', error);
        setLoading(false);
      });
  }, []);

  useEffect(() => {
    // Filter invoices based on selected filter
    if (filter === 'all') {
      setFilteredInvoices(invoices);
    } else if (filter === 'due') {
      setFilteredInvoices(invoices.filter(invoice => invoice.status === 'due'));
    }
  }, [filter, invoices]);

  const handleFilterChange = (e) => {
    setFilter(e.target.value);
  };

  if (loading) {
    return <div className="loader">Loading...</div>;
  }

  return (
    <div className="invoice-list">
      <h1>Invoices</h1>
      
      {/* Filter dropdown */}
      <div className="filter-section">
        <label htmlFor="filter">Filter Invoices:</label>
        <select id="filter" value={filter} onChange={handleFilterChange}>
          <option value="all">All Invoices</option>
          <option value="due">Due Invoices</option>
        </select>
      </div>
      
      {filteredInvoices.length === 0 ? (
        <p>No invoices found.</p>
      ) : (
        <ul>
          {filteredInvoices.map((invoice) => (
            <li key={invoice._id} className="invoice-item">
              <div className="invoice-details">
                <h2>{invoice.customer || 'Unknown Customer'}</h2>
                <p><strong>Amount:</strong> ${invoice.amount || '0'}</p>
                <p><strong>Status:</strong> {invoice.status || 'Unknown'}</p>
                <p><strong>Date:</strong> {invoice.date ? new Date(invoice.date).toLocaleDateString() : 'Unknown Date'}</p>
              </div>
            </li>
          ))}
        </ul>
      )}
    </div>
  );
};

export default InvoiceList;
